import React, {useEffect, useState} from "react";
import {NavLink, useParams} from "react-router-dom";
import {connect} from "react-redux";
import {Image, Pagination} from "antd";
import Css from "./index.module.css";
import {initToplist, setgdComment, setTopOffset, setTopRank, setTopSongs} from "./store/actions";
import Load from "@/components/load";
import {getSongsInfo} from "@/api/playlist";
import {chinaDate} from "@/untils/formatDate";
import PlayBtn from "@/components/btns/playBtn";
import CollectBtn from "@/components/btns/collectBtn";
import ShareBtn from "@/components/btns/shareBtn";
import DownloadBtn from "@/components/btns/downloadBtn";
import CommentBtn from "@/components/btns/commentBtn";
import Title from "@/components/title";
import MyTable from "@/components/myTable";
import MyComment from "@/components/myComment";
import CommentItem from "@/components/commentItem";

function TopList(props) {
    const { rank, songs, comment, offset, total, gdComment, _initToplist, _setTopRank,
        _setTopSongs, _setTopOffset, _setgdComment } = props;
    const { id } = useParams();
    const [sheet, setSheet] = useState(null);   //歌单信息
    const [current, setCurrent] = useState(1);

    useEffect(()=>{
        _initToplist();
        rank === null && _setTopRank();
        setCurrent(1);
    },[id])
    useEffect(()=>{
        if(id !== undefined){
            getSongsAll(id);
        }else if(rank !== null){
            getSongsAll(rank[0].id);
        }
    },[id,rank])

    function getSongsAll(_id) {
        getSongsInfo(_id).then(res=>{
            if(res.code === 200){
                setSheet(res.playlist);
            }
        })
        _setTopSongs(_id);
        _setgdComment(_id);
        _setTopOffset(_id,0);
    }
    //分页
    function change(_page) {
        setCurrent(_page);
        _setTopOffset(sheet.id,_page);
    }

    return (
        <div className={["vessel clearFix"].join(" ")}>
            {/*左侧导航*/}
            <div className={["clearFix float_l",Css.left].join(" ")}>
                {
                    rank !== null ?
                        <div className={Css.nav}>
                            <h2>云音乐特色榜</h2>
                            <ul>
                                {
                                    rank.slice(0,4).map((item,index)=>{
                                        return <li key={index}>
                                            <NavLink to={`/discover/toplist/${item.id}`}
                                                     className={({ isActive })=>["d_block",(isActive?Css.active:"")].join(" ")}>
                                                <div className={["clearFix",Css.list].join(" ")}>
                                                    <div className={"float_l"}>
                                                        <Image
                                                            width={40}
                                                            height={40}
                                                            src={item.coverImgUrl}
                                                            fallback="data:image/png;base64,"
                                                        />
                                                    </div>
                                                    <div className={Css.text}>
                                                        <p className={"font_h"}>{item.name}</p>
                                                        <p className={"font_h"}>{item.updateFrequency}</p>
                                                    </div>
                                                </div>
                                            </NavLink>
                                        </li>
                                    })
                                }
                            </ul>
                            <h2>全球媒体榜</h2>
                            <ul>
                                {
                                    rank.slice(4).map((item,index)=>{
                                        return <li key={index}>
                                            <NavLink to={`/discover/toplist/${item.id}`}
                                                     className={({ isActive })=>["d_block",(isActive?Css.active:"")].join(" ")}>
                                                <div className={["clearFix",Css.list].join(" ")}>
                                                    <div className={"float_l"}>
                                                        <Image
                                                            width={40}
                                                            height={40}
                                                            src={item.coverImgUrl}
                                                            fallback="data:image/png;base64,"
                                                        />
                                                    </div>
                                                    <div className={Css.text}>
                                                        <p className={"font_h"}>{item.name}</p>
                                                        <p className={"font_h"}>{item.updateFrequency}</p>
                                                    </div>
                                                </div>
                                            </NavLink>
                                        </li>
                                    })
                                }
                            </ul>
                        </div> : <Load/>
                }
            </div>
            {/*分隔栏*/}
            <div className={["line",Css.line].join(" ")}> </div>
            {/*右侧歌单*/}
            <div className={["float_r",Css.right].join(" ")}>
                {/*歌单信息*/}
                <div className={Css.songsInfo}>
                    {
                        sheet !== null ?
                            <div className={"clearFix"}>
                                {/*图片  */}
                                <div className={["c_box float_l",Css.imgCnt].join(" ")}>
                                    <Image
                                        width={150}
                                        height={150}
                                        src={sheet.coverImgUrl}
                                        fallback="data:image/png;base64,"
                                    />
                                </div>
                                {/*信息*/}
                                <div className={Css.info}>
                                    <p className={Css.sheetName}>{sheet.name}</p>
                                    <p className={["font_h",Css.update].join(" ")}>
                                        <i className={"iconfont"}>&#xe74f;</i>
                                        <span>最近更新: {chinaDate(sheet.updateTime)}</span>
                                        <span>({sheet.description})</span>
                                    </p>
                                    {/*按钮*/}
                                    <div className={Css.ctrls}>
                                        <PlayBtn id={sheet.id}/>
                                        <CollectBtn name={sheet.subscribedCount} id={sheet.id} visible={false}/>
                                        <ShareBtn id={sheet.id} count={sheet.shareCount}/>
                                        <DownloadBtn id={sheet.id}/>
                                        <CommentBtn id={sheet.id} count={sheet.commentCount}/>
                                    </div>
                                </div>
                            </div> : <Load/>
                    }
                </div>
                {/*歌单歌曲*/}
                <div className={Css.songs}>
                    {
                        (songs !== null) && (sheet !== null) ? <>
                            <Title title={"歌曲列表"}>
                                <span>{songs.length}首歌</span>
                                <span className={Css.playCount}>播放: <span>{sheet.playCount}</span> 次</span>
                            </Title>
                            <MyTable songs={songs}/>
                        </> : <Load/>
                    }
                </div>
                {/*评论*/}
                <div className={Css.commentCnt}>
                    {
                        sheet !== null &&
                            <>
                                <Title title={"评论"}>
                                    <span>共 {sheet.commentCount} 条评论</span>
                                </Title>
                                {/*我的评论*/}
                                <MyComment id={sheet.id} type={"songs"}/>
                            </>
                    }
                    {/*精彩评论列表*/}
                    {
                        (gdComment !== null && current === 1) &&
                        <div className={Css.gdComment}>
                            <h3 className={"stitle"}>精彩评论</h3>
                            <ul>
                                {
                                    gdComment.map((item,index)=>{
                                        return <li key={index} className={Css.commentItem}>
                                            <CommentItem comment={item}/>
                                        </li>
                                    })
                                }
                            </ul>
                        </div>
                    }
                    {/*最新评论列表*/}
                    {
                        sheet !== null &&
                        <div className={Css.comment}>
                            <h3 className={"stitle"}>最新评论 <span>{total}</span></h3>
                            {
                                comment[offset] !== undefined ?
                                    <ul>
                                        {
                                            comment[offset].map((item,index)=>{
                                                return <li key={index} className={Css.commentItem}>
                                                    <CommentItem comment={item}/>
                                                </li>
                                            })
                                        }
                                    </ul> : <Load/>
                            }
                            <div className={Css.page}>
                                <Pagination current={current} onChange={change} total={total} pageSize={30} showSizeChanger={false}/>
                            </div>
                        </div>
                    }
                </div>
            </div>
        </div>
    )
}

const mapState = (state)=>{
    const { rank, songs, comment, offset, total, gdComment } = state.get("toplist");
    return { rank, songs, comment, offset,total, gdComment };
}
const mapDispatch = (dispatch)=>{
    return {
        async _initToplist(){
            dispatch(initToplist());
        },
        async _setTopRank(_id){
            dispatch(setTopRank(_id));
        },
        async _setTopSongs(_id){
            dispatch(setTopSongs(_id));
        },
        async _setTopOffset(_id,_offset){
            dispatch(setTopOffset(_id,_offset));
        },
        async _setgdComment(_id){
            dispatch(setgdComment(_id));
        }
    }
}

export default connect(mapState,mapDispatch)(TopList);