import React, {useEffect, useState} from "react";
import {Link, useParams} from "react-router-dom";
import Css from "./index.module.css";
import Equip from "../../components/equip";
import {getDjInfo, getDjProgram, getPersonDj} from "../../api/djradio";
import Load from "../../components/load";
import ListItem from "../../components/listItem";
import {Image, message, Pagination, Tag} from "antd";
import SubscribeBtn from "../../components/btns/subscribeBtn";
import PlayBtn from "../../components/btns/playBtn";
import ShareBtn from "../../components/btns/shareBtn";
import Title from "../../components/title";
import {localDate} from "../../untils/formatDate";
import changeTime from "../../untils/changeTime";
import Controls from "../../components/controls";

export default function DjRadio(props) {
    const {id} = useParams();
    const [recDj, setRecDj] = useState(null);
    const [djInfo, setDjInfo] = useState(null);
    const [list, setList] = useState([]);
    const [current, setCurrent] = useState(1);
    const [total, seTotal] = useState(0);

    useEffect(()=>{
        getPersonDj().then(res=>{
            if(res.code === 200){
                setRecDj(res.data);
            }
        })
        getDjInfo(id).then(res=>{
            if(res.code === 200){
                setDjInfo(res.data);
            }
        })
        change(current);
    },[id])

    function change(_page) {
        setCurrent(_page);
        getDjProgram(id,_page-1,50).then(res=>{
            console.log(res);
            if(res.code === 200){
                list.push(res.programs);
                setList([...list]);
                seTotal(res.count);
            }
        });
    }
    function play(_id) {
        message.success(_id);
    }

    return (
        <div className={["vessel clearFix"].join(" ")}>
            {/*左侧电台*/}
            <div className={["float_l",Css.left].join(" ")}>
                {/*电台信息*/}
                {
                    djInfo !== null ?
                        <>
                            {/*信息*/}
                            <div className={["clearFix",Css.infoCnt].join(" ")}>
                                <div className={["float_l",Css.img].join(" ")}>
                                    <Image
                                        width={200}
                                        height={200}
                                        src={djInfo.picUrl}
                                        fallback="data:image/png;base64,"
                                    />
                                </div>
                                <div className={Css.info}>
                                    <div className={Css.nameCnt}>
                                        <div className={Css.tag}>
                                            <Tag color="#cd201f">
                                                电台
                                            </Tag>
                                        </div>
                                        <h2 className={["word_w",Css.name].join(" ")}>{djInfo.name}</h2>
                                    </div>
                                    <div className={Css.dj}>
                                        <Link to={`/user/home/${djInfo.dj.userId}`}>
                                            <Image
                                                width={35}
                                                height={35}
                                                src={djInfo.dj.avatarUrl}
                                                fallback="data:image/png;base64,"
                                            />
                                        </Link>
                                        <Link to={`/user/home/${djInfo.dj.userId}`}>{djInfo.dj.nickname}</Link>
                                        {
                                            djInfo.dj.avatarDetail !== null &&
                                            <img className={Css.flog} src={djInfo.dj.avatarDetail.identityIconUrl} alt="pic"/>
                                        }
                                    </div>
                                    <div className={Css.btns}>
                                        <SubscribeBtn id={djInfo.id} count={djInfo.subCount}/>
                                        <PlayBtn id={djInfo.id} name={"播放全部"}/>
                                        <ShareBtn id={djInfo.id} count={djInfo.shareCount}/>
                                    </div>
                                    <div className={Css.tags}>
                                        <div><Tag color="red">{djInfo.category}</Tag></div>
                                        <p className={"word_w"}>{djInfo.desc}</p>
                                    </div>
                                </div>
                            </div>
                            {/*列表*/}
                            <div className={Css.listCnt}>
                                <Title title={"节目列表"}>
                                    <span className={Css.small}>共 {total} 期</span>
                                </Title>
                                {
                                    list[current-1] !== undefined ?
                                        <ul className={["clearFix",Css.list].join(" ")}>
                                            {
                                                list[current-1].map((item,index)=>{
                                                    return <li key={index}>
                                                        <div className={["text_a_c c_box",Css.top].join(" ")}>
                                                            <span>{index+1}</span>
                                                            <a href={"#"} onClick={(e)=>{e.preventDefault(); play(item.id);}}
                                                               className={"iconfont"}>&#xec61;</a>
                                                        </div>
                                                        <div className={["font_h",Css.name].join(" ")}>
                                                            <Link to={`/program/${item.id}`}>{item.name.split(".")[1]}</Link>
                                                            <div className={["d_l_b",Css.ctrl].join(" ")}>
                                                                <Controls music={item}/>
                                                            </div>
                                                        </div>
                                                        <div className={["font_h c_box",Css.play].join(" ")}>
                                                            <span>播放 {item.listenerCount}</span>
                                                        </div>
                                                        <div className={["font_h c_box",Css.like].join(" ")}>
                                                            <span>点赞 {item.likedCount}</span>
                                                        </div>
                                                        <div className={["font_h c_box",Css.date].join(" ")}>
                                                            <span>{localDate(item.createTime)}</span>
                                                        </div>
                                                        <div className={["font_h c_box",Css.time].join(" ")}>
                                                            <span>{changeTime(item.duration)}</span>
                                                        </div>
                                                    </li>
                                                })
                                            }
                                        </ul> : <Load/>
                                }
                                <div className={Css.page}>
                                    <Pagination current={current} onChange={change} total={total} pageSize={50} showSizeChanger={false}/>
                                </div>
                            </div>
                        </> : <Load/>
                }
            </div>
            {/*分隔栏*/}
            <div className={["line",Css.line].join(" ")}> </div>
            {/*右侧列表*/}
            <div className={["float_r",Css.right].join(" ")}>
                <div className={Css.rec}>
                    <div className={"stitle"}>猜你喜欢</div>
                    {
                        recDj !== null ?
                            <ul className={"clearFix"}>
                                {
                                    recDj.map((item,index)=>{
                                        return <li key={index} className={"float_l"}>
                                            <ListItem path={`/djradio/${item.id}`} imgUrl={item.picUrl}>
                                                <p className={"font_h"}><Link to={`/djradio/${item.id}`}>{item.name}</Link></p>
                                                <p  className={Css.dj}>
                                                    <span>by</span>
                                                    <Link to={`/user/home/${item.dj.userId}`}
                                                        className={"d_block font_h"} style={{"maxWidth":"100px"}}>
                                                        {item.dj.nickname}</Link>
                                                    {
                                                        item.dj.avatarDetail !== null &&
                                                        <img className={Css.tag} style={{"width":"12px","height":"12px"}}
                                                             src={item.dj.avatarDetail.identityIconUrl} alt="pic"/>
                                                    }
                                                </p>
                                            </ListItem>
                                        </li>
                                    })
                                }
                            </ul> : <Load/>
                    }
                </div>
                <Equip/>
            </div>
        </div>
    )
}